package week1.day2Assignments;

public class CheckNumberIsPositive {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var=10;
		
		if (var>0)
			System.out.println("Number "+ var + " is Positive");
		else if (var == 0)
			System.out.println("Number "+ var + " is Zero");
		else
			System.out.println("Number "+ var + " is Negative");
		
		
	}

}
